INSERT INTO `empreendimentos` VALUES ('1', '5', 'Residencial Palmeiras', 'residencial-palmeiras', '<p class=\"\">\r\n                Esta é a chance para você sair do aluguel e morar no 1º condomínio de casas de Pirapozinho. <br>\r\n                <span>Documentação 100% grátis + desconto de R$ 49.445,00.</span>\r\n            </p>', 'a6769451dd4f05bfb263f504ec05599748512366.png', '1', 'Residencial Palmeiras', 'Residencial Palmeiras', 'Residencial Palmeiras', '1', '0', '2025-07-23 21:18:30', '2025-08-04 20:21:06', null, '848dad7f8aed044b786ffebd8ca5d760f48ab5c6.png', 'Pirapozinho/SP', '1');
INSERT INTO `empreendimentos` VALUES ('2', '4', 'Residencial Bela Vista II', 'residencial-bela-vista-ii', '<p>Aproveite essa chance para sair de vez do aluguel e morar em um dos melhores endereços da cidade, além de contar com uma infraestrutura completa, desenvolvida por quem mais entende de viver bem</p>', '1303769ca8ac93740aadf250700694037e0ff84e.png', '0', 'Residencial Bela Vista II', 'Residencial Bela Vista II', 'Residencial Bela Vista II', '1', '1', '2025-07-27 16:46:25', '2025-08-04 21:02:00', null, '40d3d0638719455cca2b52861a4a1895a2fa7123.png', 'Três Lagoas/MS', '0');
INSERT INTO `empreendimentos` VALUES ('3', '3', 'Residencial Bela Vista I', 'residencial-bela-vista-i', '<p>O condomínio Residencial Bela Vista I da Ind Empreendimentos foi projetado para suprir cada uma de suas necessidades e da sua família, para que você possa viver dias de tranquilidade e ter a garantia de segurança que tanto precisa.</p>', 'b38fb5c14d144e80b91b66b76b8d1a9306bad6e3.png', '0', 'Residencial Bela Vista I', 'Residencial Bela Vista I', 'Residencial Bela Vista I', '1', '2', '2025-07-27 16:55:23', '2025-07-31 19:46:24', null, '8179fcbba15940c68f48aa5aa937b0419491273a.png', 'Três Lagoas/MS', '0');
INSERT INTO `empreendimentos` VALUES ('4', '2', 'Residencial das Palmeiras', 'residencial-das-palmeiras', '<p>314 lotes projetados pela Ind Empreendimentos com infraestrutura completa para suprir cada uma de suas necessidades e da sua família, para que você possa viver dias agradáveis com quem você ama e ter a garantia de tranquilidade que tanto precisa.</p>', '401c8218361589fa5eff074679aff4f8bd4cea7a.png', '0', 'Residencial das Palmeiras', 'Residencial das Palmeiras', 'Residencial das Palmeiras', '1', '3', '2025-07-27 17:07:26', '2025-07-27 17:07:26', null, '5b686c0f211e6e69f3989e7ed67425a13069de24.png', 'Indiana/SP', '0');

